import React from "react";
import Card from "react-bootstrap/Card";
import CardDeck from "react-bootstrap/CardDeck";
import Image from "react-bootstrap/Image";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./skills_style.css";
import { skills } from "./skills_info";
import { Container } from "react-bootstrap";

const Skills = () => {
    return (
        <div className="pt-3 pb-3" id="skills">
            <h1 className="text-center font-details-b pd-r">Things I'm sometimes good at</h1>
                <Container>
                    <Row className="pt-3 pb-5 align-items-center">
                        <Col xs={12} md={6}>
                            <Card className="focus mt-2 mb-2">
                                <Card.Body>
                                    <Card.Title className="text-center card-title">Languages</Card.Title>
                                    <hr />
                                    <Card.Text className="card-text d-flex justify-content-start flex-column">
                                        {skills.languages.map((skill, index) => (
                                            <span className="p-2" key={index}>
                                            <a className="text-dark text-decoration-none" target="_blank" rel="noopener noreferrer">
                                                <Image src={skill.img} alt={skill.img_alt} rounded className="image-style m-1"></Image> {skill.name}
                                            </a>
                                            </span>
                                        ))}
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>
                </Container>
        </div>
    );
};

export default Skills;
