import React from "react";
import Arrow from "../Down-Arrow/arrow";
import homebg from "../../assets/imgs/homebg.png"
import "./homestyle.css"
import styled from "styled-components";

const home_img = styled.h1`
  position: absolute;
  width: 100%;
  top: 22rem;
  z-index: 1;
  margin-top: -50px;
  text-align: center;
  strong {
    font-size: 1.25em;
  }
  div {
    color: ${props => props.theme.background};
    text-shadow: 0px 2px 5px rgba(0, 0, 0, 0.4);
    font-weight: 100;
    letter-spacing: 7px;
    .main {
      font-size: 50px;
    }
  }
`;

const Homeimg = () => {
  return (
    <div id="home">
      <img className="d-block w-100 custom-img" src={homebg} alt="Home Background" />
    </div>
  );
};

export default Homeimg;
